import{_ as o,c as e,o as t,a8 as l}from"./chunks/framework.CBChqOMV.js";const _=JSON.parse('{"title":"常见 terminal 快捷键","description":"","frontmatter":{},"headers":[],"relativePath":"notes/linux/common-terminal-shortcuts.md","filePath":"notes/linux/common-terminal-shortcuts.md","lastUpdated":1718078111000}'),r={name:"notes/linux/common-terminal-shortcuts.md"},c=l('<h1 id="常见-terminal-快捷键" tabindex="-1">常见 terminal 快捷键 <a class="header-anchor" href="#常见-terminal-快捷键" aria-label="Permalink to &quot;常见 terminal 快捷键&quot;">​</a></h1><p>在 <code>Linux</code> 终端中，你可以使用以下快捷键来移动光标和进行其他常见操作。这些快捷键通常适用于 <code>Bash</code> <code>shell</code> 和其他大多数常见的 <code>shell</code>：</p><h3 id="光标移动" tabindex="-1">光标移动 <a class="header-anchor" href="#光标移动" aria-label="Permalink to &quot;光标移动&quot;">​</a></h3><ul><li><strong><code>Ctrl + A</code></strong>：移动光标到行首。</li><li><strong><code>Ctrl + E</code></strong>：移动光标到行尾。</li><li><strong><code>Ctrl + B</code></strong>：向左移动光标一个字符。</li><li><strong><code>Ctrl + F</code></strong>：向右移动光标一个字符。</li><li><strong><code>Alt + B</code></strong>：向左移动光标一个词。</li><li><strong><code>Alt + F</code></strong>：向右移动光标一个词。</li></ul><h3 id="删除文本" tabindex="-1">删除文本 <a class="header-anchor" href="#删除文本" aria-label="Permalink to &quot;删除文本&quot;">​</a></h3><ul><li><strong><code>Ctrl + D</code></strong>：删除光标所在位置的字符。</li><li><strong><code>Ctrl + H</code></strong>：删除光标前面的字符（和 <code>Backspace</code> 键相同）。</li><li><strong><code>Ctrl + W</code></strong>：删除光标前一个词。</li><li><strong><code>Alt + D</code></strong>：删除光标后的一个词。</li><li><strong><code>Ctrl + K</code></strong>：删除从光标到行尾的所有字符。</li><li><strong><code>Ctrl + U</code></strong>：删除从光标到行首的所有字符。</li></ul><h3 id="文本操作" tabindex="-1">文本操作 <a class="header-anchor" href="#文本操作" aria-label="Permalink to &quot;文本操作&quot;">​</a></h3><ul><li><strong><code>Ctrl + Y</code></strong>：粘贴（<code>yank</code>）最后一次被删除的文本。</li><li><strong><code>Alt + .</code></strong>：插入上一个命令的最后一个参数。</li></ul><h3 id="其他" tabindex="-1">其他 <a class="header-anchor" href="#其他" aria-label="Permalink to &quot;其他&quot;">​</a></h3><ul><li><strong><code>Ctrl + L</code></strong>：清屏（相当于 <code>clear</code> 命令）。</li><li><strong><code>Ctrl + R</code></strong>：反向搜索命令历史。</li><li><strong><code>Ctrl + C</code></strong>：终止当前正在运行的命令。</li><li><strong><code>Ctrl + Z</code></strong>：暂停当前正在运行的命令并放入后台（使用 <code>fg</code> 恢复）。</li><li><strong><code>Ctrl + P</code></strong>：向上查看命令历史。</li><li><strong><code>Ctrl + N</code></strong>：向下查看命令历史。</li></ul><p>这些快捷键可以帮助你更高效地在命令行中编辑和导航文本。如果你在使用某个特定的终端模拟器（如 <code>GNOME Terminal</code>, <code>Konsole</code> 等），确保它们没有覆盖这些默认的快捷键。</p>',11),n=[c];function a(d,i,s,g,h,m){return t(),e("div",null,n)}const C=o(r,[["render",a]]);export{_ as __pageData,C as default};
